package com.example.helloworld;

public class Hello {

	int id;
	String name;
	
	

	

	public Hello(String name2, int id2) {
		// TODO Auto-generated constructor stub
		id=id2;
		name=name2;
	}



	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}
	
	
}

